import React from "react"
import * as ReactDOMClient from "react-dom/client"


class Header extends React.Component { //компонент (новый формат,правильно)
    render() {
        return (
            <header>{this.props.title}</header>
        )
    }
}

class App extends React.Component { //компонент (константа)
    helpText = "Help text!"
    render(){
        return(<div className = "name"> 
            <Header title = "Шапка сайта"/> {/* компонент в компоненте */}
            <h1>{this.helpText}</h1>
            <input placeholder={this.helpText}  //создаём поле для ввода 
            onClick = {this.inputClick} onMouseEnter = {this.MousOver}/> 
            <p>{this.helpText === "Help text!" ? "Yes" : "No"}</p> 
            </div>)
        }

        inputClick() {console.log("Clicked")}
        MousOver() {console.log("Mouse Over")}
}

const app = ReactDOMClient.createRoot(document.getElementById("app"))//находим div с тегом app и помещаем в переменную
//новый способ для вывода информации
app.render(<App />) //помещаем во внутрь тега div компонент App
