import React from "react"
import Header from "./components/Header"
import Image from "./components/img"
import logo from "./img/logo.png"

class App extends React.Component { //компонент (константа)
    constructor(props) {
        super(props)//конструскор родительского класса (props = свойвства)
        this.state = {   //(state = состояние)
            helpText: "Help text", //сосотояние1
            userData: ""               //сосотояние2
        } 
        
        this.inputClick = this.inputClick.bind(this)//inputClick - сможет взаемодействовать с состояниями
    }

    render() {
        return(<div className = "name"> 
            <Header title = "Шапка сайта" name="" /> {/* компонент в компоненте */}
            <h1>{this.state.helpText}</h1>
            <h2>{this.state.userData}</h2>
            <input placeholder={this.state.helpText}  //создаём поле для ввода 
                onChange={event => this.setState({userData: even.target.value})}
                onClick = {this.inputClick} onMouseEnter = {this.mousOver}/> 
            <p>{this.state.helpText === "Help text!" ? "Yes" : "No"}</p> 
            <Image image = {logo}/>
            <img src={logo}/>
        </div>)
        }

        inputClick() {
            this.setState({ helpText: "Changed" })//для измениения состояния (меняем helpText на Changed)
            console.log("Clicked")
        }
        mousOver() {console.log("Mouse Over")}
}

export default App
