import React from "react"
import * as ReactDOMClient from "react-dom/client"

const inputClick = () => console.log("Clicked") //создается функциия в переменной
const MousOver = () => console.log("Mouse Over")

const helpText = "Help text!"

class Header extends React.Component { //компонент (новый формат,правильно)
    render() {
        return (
            <header>Шапка сайта</header>
        )
    }
}

class App extends React.Component { //компонент (константа)
    render(){
        return(<div className = "name"> 
        <Header /> {/* компонент в компоненте */}
        <h1>{helpText}</h1>
        <input placeholder={helpText}  //создаём поле для ввода 
        onClick = {inputClick} onMouseEnter = {MousOver}/> 
        <p>{helpText === "Help text!" ? "Yes" : "No"}</p> 
        </div>)
    }
}

const app = ReactDOMClient.createRoot(document.getElementById("app"))//находим div с тегом app и помещаем в переменную
//новый способ для вывода информации
app.render(<App />) //помещаем во внутрь тега app константу  elements
