import React from "react"

class Users extends React.Component { //компонент (новый формат,правильно)
    isers = [
        // {
        //     id: 1,
        //     firstname: 'Bob',
        //     lastname: 'Marley',
        //     bio: 'Lorem ipsum dolor sit amet consectetur, adipisicing elit.',
        //     age: 40,
        //     isHappy: true
        // },
        // {
        //     id: 2,
        //     firstname: 'John',
        //     lastname: 'Doe',
        //     bio: 'Lorem ipsum dolor sit amet consectetur, adipisicing elit.',
        //     age: 22,
        //     isHappy: false
        // }
    ]
    render() {
        const elements = (el) =>(<div className="user" key={el.id}>
            <h3>{el.firstname} {el.lastname}</h3>
            <p>{el.bio}</p>
        </div>)
        if(this.isers.length > 0)
            return (<div>
                {this.isers.map(elements)}   
            </div>)
        else 
            return (<div className="iser">
                    <h3>Пользователей нет</h3>
                </div>)
    }
}

export default Users
